void
ascii_to_utf16_scalar(uint16_t *restrict dest, uint8_t const *restrict src, size_t len)
{
	while (len--) BENCH_CLOBBER(), *dest++ = *src++;
}

void
ascii_to_utf32_scalar(uint32_t *restrict dest, uint8_t const *restrict src, size_t len)
{
	while (len--)  BENCH_CLOBBER(), *dest++ = *src++;
}

size_t
b64_encode_scalar(uint8_t *dst, const uint8_t *src, size_t length, const uint8_t LUTs[64+16])
{
	uint8_t *dstBeg = dst;
	for (; length >= 3; length -= 3, src += 3, dst += 4) {
		uint32_t u32 = src[0] << 16 | src[1] << 8 | src[2];
		dst[0] = LUTs[(u32 >> 18) & 63];
		dst[1] = LUTs[(u32 >> 12) & 63];
		dst[2] = LUTs[(u32 >>  6) & 63];
		dst[3] = LUTs[(u32 >>  0) & 63];
	}
	if (length > 0) {
		uint32_t u32 = src[0] << 8 | (length > 1 ? src[1] : 0);
		*dst++ =              LUTs[(u32 >> 10) & 63];
		*dst++ =              LUTs[(u32 >>  4) & 63];
		*dst++ = length > 1 ? LUTs[(u32 <<  2) & 63] : '=';
		*dst++ =                                       '=';
	}
	return dst - dstBeg;
}

void
byteswap32_scalar(uint32_t *ptr, size_t n)
{
	for (uint8_t *p = (uint8_t*)ptr; n--; p += 4) {
		uint8_t p0 = p[0], p1 = p[1], p2 = p[2], p3 = p[3];
		p[3] = p0; BENCH_CLOBBER();
		p[2] = p1; BENCH_CLOBBER();
		p[1] = p2; BENCH_CLOBBER();
		p[0] = p3; BENCH_CLOBBER();
	}
}

void
hist_scalar(uint32_t *hist, float *x, float *y, size_t n)
{
	for (size_t i = 0; i < n; ++i) {
		float dist = x[i]*x[i] + y[i]*y[i];
		__asm__ ("fsqrt.s %0, %0\n" : "+f"(dist));
		size_t idx = dist;
		idx = idx > 100 ? 100 : dist;
		++hist[idx];
	}
}

void
LUT4_scalar(uint8_t lut[16], uint8_t *ptr, size_t n)
{
	for (; n--; ++ptr)
		*ptr = lut[*ptr & 0xF], BENCH_CLOBBER();
}

void
LUT6_scalar(uint8_t lut[64], uint8_t *ptr, size_t n)
{
	for (; n--; ++ptr)
		*ptr = lut[*ptr & 63], BENCH_CLOBBER();
}

void *
memcpy_scalar(void *restrict dest, void const *restrict src, size_t n)
{
	unsigned char *d = dest;
	unsigned char const *s = src;
	while (n--) *d++ = *s++, BENCH_CLOBBER();
	return dest;
}

void
memreverse_scalar(uint8_t *restrict dest, uint8_t const *restrict src, size_t n)
{
	for (size_t i = 0; i < n; ++i)
		dest[i] = src[n-i-1], BENCH_CLOBBER();
}

void *
memset_scalar(void *dest, int c, size_t n)
{
	unsigned char *d = dest;
	while (n--) *d++ = c, BENCH_CLOBBER();
	return dest;
}

size_t
mergelines_scalar(char *str, size_t len)
{
	char *dest = str;
	char *src = str;

	while (len > 1) {
		if (src[0] == '\\' && src[1] == '\n')
			src += 2, len -= 2;
		else
			*dest++ = *src++, --len;
		BENCH_CLOBBER();
	}
	if (len > 0)
		*dest++ = *src++;
	return dest - str;
}

size_t
strlen_scalar(char const *s)
{
	char const *a = s;
	while (*s) ++s, BENCH_CLOBBER();
	return s - a;
}

size_t
utf8_count_scalar(char const *str, size_t len)
{
	uint8_t const *p = (uint8_t const*)str;
	size_t count = 0;
	while (len--) count += (*p++ & 0xc0) != 0x80, BENCH_CLOBBER();
	return count;
}
